import React from 'react'
import knowmynation from "../assets/knowmynation.png" ;
import hozotech from "../assets/hozo.png";
const Projects = () => {
    const projects =[
        {
            id:1,
            src:knowmynation,
            href:'https://knowmynation.netlify.app/',
            code:'https://github.com/Vijay-Hozo/know-my-nation'
        },
        {
            id:2,
            src:hozotech,
            href:'https://hozotech.netlify.app/',
            code:'https://github.com/Vijay-Hozo/OCTANET_SEPTEMBER'
        },
    ]
  return (
    
    <div name="Projects" className='bg-gradient-to-b from-black to-gray-800 w-full text-white md:h-screen'>
        <div classname="max-w-screen-lg p-4 mx-auto flex flex-col justify-center w-full h-full">
            <div className='pb-8'>
                <p className='text-4xl font-bold border-b-4 border-gray-500 p-2 inline'>Projects</p>
                <p className='py-6'>Check out some of my work right here!</p>
            </div>

            <div className='grid sm:grid-cols-2 md:grid-cols-2 gap-8 px-12 sm:px-0'>

            {
                projects.map(({id,src,href,code})=>(
                    
                    <div key={id} className='shadow-md shadow-gray-600 rounded-lg'>
                    <img src={src} alt=""  className='rounded-md duration-200 hover:scale-105'/>
                    <div className='flex items-center justify-center'>
                        <a
                        href={href}
                        className='w-1/2 px-6 py-3 m-4 duration-200 hover:scale-105'>Demo</a>
                        <a href={code} className='w-1/2 px-6 py-3 m-4 duration-200 hover:scale-105'>Code</a>
                    </div>
                </div>
            
                ))
            }

            </div> 
        </div>
    </div>
  )
}

export default Projects
