import Portfolioamico from "../assets/Portfolio-amico.png";
const Home = () => {
  return (
    <div 
    name="home"
    className='h-screen text-white w-full bg-gradient-to-b from-black to-gray-800'>
        
        <div className='max-w-screen-lg mx-auto flex flex-col items-center justify-center h-full px-4 md:flex-row'>
            <div className=' flex flex-col justify-center h-full'>
                <h2>I'm</h2>
                <h3 className='text-4xl sm:text-7xl text- font-bold'>VIJAY A</h3>
                <h2 className='text-1xl sm:text-3xl text-white font-semi-bold'>FRONTEND DEVELOPER</h2>                
            </div>
        </div>
        /<div>
            <img src={Portfolioamico} alt="my personal" className='size-50px rounded-10xl mx-auto w-2/3 md:w-full' />
        </div>//
    </div>
  );
};

export default Home;
